<template>
	<v-card :loading="loading" class="mx-auto my-12" max-width="274">
		<template slot="progress">
			<v-progress-linear color="deep-purple" height="10" indeterminate></v-progress-linear>
		</template>

		<v-img
			height="180"
			src="data:image/jpeg;base64,"
		></v-img>

		<v-card-title>{{ name }}</v-card-title>

		<v-divider class="mx-4"></v-divider>

		<v-card-text>
			<v-text>${{ price }}</v-text>
			<v-chip-group v-model="selection" active-class="deep-purple accent-4 white--text" multiple column>
				<v-chip v-for="(tag, idx) in hashTag" :key="idx">{{ tag }}</v-chip>
			</v-chip-group>

			<v-chip-group v-model="selection" active-class="red white--text" multiple>
				<v-chip v-for="(tag, idx) in hashTag" :key="idx" disabled>{{ tag }}</v-chip>
			</v-chip-group>
		</v-card-text>
	</v-card>
</template>

<script lang="ts">
import { Component, Vue, Prop } from 'vue-property-decorator'

type Tag = {
	code: []
	value: []
}

@Component({})
export default class Card extends Vue {
	@Prop({ default: '크라우즈' }) readonly name!: string
	@Prop({ default: 3.2 }) readonly grade!: number
	@Prop({ default: 10 }) readonly votes!: number
	@Prop({ default: 0 }) readonly price!: number
	@Prop({ default: '설명------' }) readonly describe!: string
	loading = false
	selection = [1, 2]
	skill = 20
	hashTag = ['플랫포머', '액션', '캐주얼', 'RPG']
	gameTag = ['PC', 'Mobile', 'Demo']
	reserve() {
		this.loading = true

		setTimeout(() => (this.loading = false), 2000)
	}
}
</script>
